import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars , faTimes} from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import SideBar from './SideBar'; // Import the SideBar component
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js/auto';

function Analysis() {
    const navigate = useNavigate();
    const [sidebarOpen, setSidebarOpen] = useState(true);
    const [inputtedAudio, setInputtedAudio] = useState(null);
    const [loading, setLoading] = useState(false);
    const [employeeData, setEmployeeData] = useState({ firstName: '' });
    const [analysisData, setAnalysisData] = useState([]);
    const [isAnalysisComplete, setIsAnalysisComplete] = useState(false);

    let audioFiles = null;
    let chartInstance = null;

    let yourTestAnalysisData = [[{'id': 0, 'seek': 0, 'start': 0.0, 'end': 2.0, 'text': " Hello, I'm speaking to Amma.", 'emotion' : 'confused', 'tokens': [50364, 2425, 11, 286, 478, 4124, 281, 2012, 1696, 13, 50464], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}, {'id': 1, 'seek': 0, 'start': 4.5200000000000005, 'end': 8.66, 'text': ' This is Prithvi calling from Boston Institute of Analytics at Madhavalli campus.','emotion': 'happy', 'tokens': [50590, 639, 307, 2114, 355, 4917, 5141, 490, 12333, 9446, 295, 25944, 412, 5326, 71, 706, 336, 72, 4828, 13, 50797], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}], [{'id': 0, 'seek': 0, 'start': 0.0, 'end': 2.0, 'text': " Hello, I'm speaking to Amma.", 'emotion' : 'angry', 'tokens': [50364, 2425, 11, 286, 478, 4124, 281, 2012, 1696, 13, 50464], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}, {'id': 1, 'seek': 0, 'start': 4.5200000000000005, 'end': 8.66, 'text': ' This is Prithvi calling from Boston Institute of Analytics at Madhavalli campus.', 'emotion' : 'neutral', 'tokens': [50590, 639, 307, 2114, 355, 4917, 5141, 490, 12333, 9446, 295, 25944, 412, 5326, 71, 706, 336, 72, 4828, 13, 50797], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}]]

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const handleEmployeeDataChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData({
            ...employeeData,
            [name]: value,
        });
    };

    function processData(data) {
        let speaker1Data = [], speaker2Data = [];

        data.forEach(call => {
            call.forEach(segment => {
                if (segment.speaker === 'SPEAKER 1') {
                    speaker1Data.push({ time: segment.start, emotion: segment.emotion });
                } else if (segment.speaker === 'SPEAKER 2') {
                    speaker2Data.push({ time: segment.start, emotion: segment.emotion });
                }
            });
        });

        return { speaker1Data, speaker2Data };
    }

    function prepareGraphData(speakerData) {
        const labels = speakerData.map(d => `${d.time}`);
        const data = speakerData.map(d => emotionToValue(d.emotion));
        return { labels, data };
    }

    function emotionToValue(emotion) {
        switch (emotion) {
            case 'happy': return 1;
            case 'angry': return 2;
            case 'confused': return 3;
            case 'neutral': return 4;
            default: return 0;
        }
    }

    function renderGraph(speakerGraphData, canvasId) {
        if (window[canvasId]) {
            window[canvasId].destroy(); // Destroy the existing chart if it exists
        }
    
        const ctx = document.getElementById(canvasId).getContext('2d');
    
        // Convert emotion strings to numerical values for plotting
        const emotions = {
            'happy': 1,
            'angry': 2,
            'confused': 3,
            'neutral': 4
        };
    
        // Preparing data for the chart
        const chartData = {
            labels: speakerGraphData.map(d => `${d.time}`),
            datasets: [{
                label: 'Emotion Over Time',
                data: speakerGraphData.map(d => emotions[d.emotion] || 0),
                borderColor: 'blue',
                backgroundColor: 'rgba(59, 130, 246, 0.5)',
                fill: false,
                tension: 0.1
            }]
        };
    
        // Chart options
        const chartOptions = {
            scales: {
                x: {
                    type: 'linear', // Change to 'linear' to handle numeric time values
                    position: 'bottom',
                    title: {
                        display: true,
                        text: 'Time (seconds)'
                    }
                },
                y: {
                    beginAtZero: true,
                    title: {
                        display: true,
                        text: 'Emotion'
                    },
                    ticks: {
                        // Convert back numeric values to emotion strings for y-axis labels
                        callback: function(value, index, values) {
                            return Object.keys(emotions).find(key => emotions[key] === value);
                        }
                    }
                }
            },
            plugins: {
                legend: {
                    display: true
                }
            }
        };
    
        // Creating a new chart instance
        window[canvasId] = new Chart(ctx, {
            type: 'line',
            data: chartData,
            options: chartOptions
        });
    }

    const performAnalysis = async () => {
        setLoading(true);
        const success = await sendAudioFilesToBackend();

        if (success) {
            // Simulate fetching analysis data
            // Replace with actual API call to fetch analysis data
            setTimeout(() => {
                setAnalysisData(yourTestAnalysisData); // Set your test analysis data here
                setIsAnalysisComplete(true);
                setLoading(false);
            }, 3000);
        } else {
            alert('Failed to upload audio files');
            setLoading(false);
        }
    };

    let speaker1Graph, speaker2Graph;
    if (isAnalysisComplete) {
        const { speaker1Data, speaker2Data } = processData(analysisData);
        const speaker1GraphData = prepareGraphData(speaker1Data);
        const speaker2GraphData = prepareGraphData(speaker2Data);

        speaker1Graph = renderGraph(speaker1GraphData, 'speaker1Canvas');
        speaker2Graph = renderGraph(speaker2GraphData, 'speaker2Canvas');

    }

    const handleAudioFileChange = (e) => {
        audioFiles = e.target.files;
        setInputtedAudio(audioFiles);
    };

    const sendAudioFilesToBackend = async () => {
        const formData = new FormData();
        for (const file of inputtedAudio) {
            formData.append('audioFiles', file);
        }

        try {
            const response = await axios.post('http://localhost:5000/upload-audio', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            console.log(response);

            if (response.status === 200) {
                console.log('Audio files uploaded successfully');
                return true;
            } else {
                console.error('Failed to upload audio files');
                return false;
            }
        } catch (error) {
            console.error('An error occurred while uploading audio files:', error);
            return false;
        }
    };

    // const handleUploadAudio = async () => {
    //     setLoading(true);
    //     const success = await sendAudioFilesToBackend();

    //     if (success) {
    //         alert('Audio files uploaded and processed');
    //     } else {
    //         alert('Failed to upload audio files');
    //     }

    //     setLoading(false);
    // };

    return (
        <div className="flex">
            <SideBar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />

            {/* Main content area */}
            <div className={`flex-1 p-10 transition-margin duration-300 ease-in-out ${sidebarOpen ? 'ml-64' : 'ml-0'}`}>
            <button onClick={toggleSidebar} className="text-white absolute top-0 right-0 mt-2 mr-2">
                    <FontAwesomeIcon icon={sidebarOpen ? faTimes : faBars} size="lg" />
            </button>
                {/* Analysis Content */}
                <h1 className="text-2xl font-bold mb-4">Analysis Content</h1>
                <div>
                    <h2 className="text-xl mb-3">Audio Upload</h2>
                    <input
                        type="file"
                        name="audioFiles"
                        accept=".wav, .mp3"
                        multiple
                        onChange={handleAudioFileChange}
                        className="mb-3"
                    />
                    <button onClick={performAnalysis} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                        Upload and Analyze Audio
                    </button>
                </div>
                <div>
                {isAnalysisComplete && (
                <div className="mt-10">
                    <div className="mb-8">
                        {speaker1Graph}
                    </div>
                    <div>
                        {speaker2Graph}
                    </div>
                    </div>
                )}
                </div>
            </div>

            {/* Loading Overlay */}
            {loading && (
                <div className="absolute top-0 left-0 w-full h-full bg-gray-800 bg-opacity-50 flex justify-center items-center z-40">
                    <div className="loader">Loading...</div>
                </div>
            )}

        </div>
    );
}

export default Analysis;
