import React, { useRef, useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import SideBar from './SideBar'; // Import the SideBar component
import axios from 'axios';
function EmployeeManagement() {
    const [employees, setEmployees] = useState([]);
    const [newEmployee, setNewEmployee] = useState({
        firstName: '',
        lastName: '',
        email: '',
        number: '',
        dob: '',
        gender: '',
        address: ''
    });

    useEffect(() => {
        const fetchEmployees = async () => {
            const res = await axios.get('/api/employees');
            setEmployees(res.data);
        };
        fetchEmployees();
    }, []);

    const handleChange = (e) => {
        setNewEmployee({ ...newEmployee, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('/api/employees', newEmployee);
        setEmployees([...employees, newEmployee]);
        setNewEmployee({
            firstName: '',
            lastName: '',
            email: '',
            number: '',
            dob: '',
            gender: '',
            address: ''
        });
    };

    return (
        <div className="container mx-auto p-4">
            <SideBar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />

            <div className={`flex-1 p-10 ${sidebarOpen ? 'ml-64' : 'ml-0'}`}>
                <button onClick={toggleSidebar} className="text-white absolute top-0 right-0 mt-2 mr-2">
                    <FontAwesomeIcon icon={sidebarOpen ? faTimes : faBars} size="lg" />
                </button>
            </div>
            <h1 className="text-2xl font-bold mb-4">Employee Management</h1>
            <div className="mb-8">
                <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-6">
                    {/* Input fields for employee details */}
                    {/* Include other fields similarly */}
                    <input
                        type="text"
                        name="firstName"
                        value={newEmployee.firstName}
                        onChange={handleChange}
                        placeholder="First Name"
                        className="border p-2"
                    />
                    {/* ... other input fields ... */}
                    <button type="submit" className="bg-blue-500 text-white p-2 rounded">
                        Add Employee
                    </button>
                </form>
            </div>
            <div>
                <h2 className="text-xl font-semibold mb-2">Employees List</h2>
                <div className="border p-4">
                    {employees.map((employee, index) => (
                        <div key={index} className="mb-2">
                            <div>{employee.firstName} {employee.lastName}</div>
                            {/* ... display other employee details ... */}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default EmployeeManagement;
