import React, { useRef, useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import SideBar from './SideBar'; // Import the SideBar component
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js/auto';

function Analysis() {
    const navigate = useNavigate();
    const [sidebarOpen, setSidebarOpen] = useState(true);
    const [inputtedAudio, setInputtedAudio] = useState(null);
    const [loading, setLoading] = useState(false);
    const [analysisData, setAnalysisData] = useState([]);
    const [isAnalysisComplete, setIsAnalysisComplete] = useState(false);
    const [callsData, setCallsData] = useState([]);
    const [selectedCall, setSelectedCall] = useState('');
    const emotions = { 'happy': 1, 'angry': 2, 'confused': 3, 'neutral': 4 };

    let yourTestAnalysisData = [
        [
            { id: 0, start: 0.0, end: 2.0, text: "Hello, I'm speaking to Amma.", emotion: 'confused', speaker: 'SPEAKER 2' },
            { id: 1, start: 4.52, end: 8.66, text: 'This is Prithvi calling from...', emotion: 'happy', speaker: 'SPEAKER 2' }
        ],
        // Call 2 Data
        [
            { id: 0, start: 0.0, end: 2.0, text: "Hello, I'm speaking to Amma.", emotion: 'angry', speaker: 'SPEAKER 2' },
            { id: 1, start: 4.52, end: 8.66, text: 'This is Prithvi calling from...', emotion: 'neutral', speaker: 'SPEAKER 2' }
        ]
    ];

    useEffect(() => {
        if (isAnalysisComplete) {
            const { speaker1Data, speaker2Data } = processData(yourTestAnalysisData);
            const speaker1GraphData = prepareGraphData(speaker1Data);
            const speaker2GraphData = prepareGraphData(speaker2Data);

            renderGraph(speaker1GraphData, 'speaker1Canvas');
            renderGraph(speaker2GraphData, 'speaker2Canvas');
        }
        setCallsData(processAnalysisData(yourTestAnalysisData));
    }, [isAnalysisComplete]);

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const processData = (data) => {
        let speaker1Data = [], speaker2Data = [];

        data.forEach(call => {
            call.forEach(segment => {
                if (segment['speaker'] === 'SPEAKER 1') {
                    speaker1Data.push({ time: segment.start, emotion: segment.emotion });
                } else if (segment['speaker'] === 'SPEAKER 2') {
                    speaker2Data.push({ time: segment.start, emotion: segment.emotion });
                }
            });
        });

        return { speaker1Data, speaker2Data };
    }

    const prepareGraphData = (speakerData) => {
        const labels = speakerData.map(d => `${d.time}`);
        const data = speakerData.map(d => emotionToValue(d.emotion));
        return { labels, data };
    }

    const emotionToValue = (emotion) => {
        switch (emotion) {
            case 'happy': return 1;
            case 'angry': return 2;
            case 'confused': return 3;
            case 'neutral': return 4;
            default: return 0;
        }
    }

    const renderGraph = (speakerGraphData, canvasId) => {
        const ctx = document.getElementById(canvasId).getContext('2d');
        
        const chartData = {
            labels: speakerGraphData.labels,
            datasets: [{
                label: 'Emotion Over Time',
                data: speakerGraphData.data,
                borderColor: 'rgb(75, 192, 192)',
                tension: 0.1,
                fill: false
            }]
        };

        const chartOptions = {
            scales: {
                x: {
                    type: 'linear',
                    position: 'bottom'
                },
                y: {
                    beginAtZero: true,
                    ticks: {
                        stepSize: 1,
                        callback: function(value) {
                            return Object.keys(emotions).find(key => emotions[key] === value);
                        }
                    }
                }
            }
        };

        new Chart(ctx, {
            type: 'line',
            data: chartData,
            options: chartOptions
        });
    };

    const handleAudioFileChange = (e) => {
        setInputtedAudio(e.target.files);
    };

    const performAnalysis = async () => {
        setLoading(true);
        // Simulated data processing
        setTimeout(() => {
            setAnalysisData(yourTestAnalysisData);
            setIsAnalysisComplete(true);
            setLoading(false);
        }, 3000);
    };

    return (
        <div className="flex">
            <SideBar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />

            <div className={`flex-1 p-10 ${sidebarOpen ? 'ml-64' : 'ml-0'}`}>
                <button onClick={toggleSidebar} className="text-white absolute top-0 right-0 mt-2 mr-2">
                    <FontAwesomeIcon icon={sidebarOpen ? faTimes : faBars} size="lg" />
                </button>

                <h1 className="text-2xl font-bold mb-4">Analysis Content</h1>
                <div>
                    <h2 className="text-xl mb-3">Audio Upload</h2>
                    <input type="file" multiple onChange={handleAudioFileChange} className="mb-3" />
                    <button onClick={performAnalysis} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                        Upload and Analyze Audio
                    </button>
                </div>

                {isAnalysisComplete && (
                    <>
                        <canvas class="resize-none md:resize" id="speaker1Canvas"></canvas>
                        <canvas class="resize-none md:resize" id="speaker2Canvas"></canvas>
                    </>
                )}
            </div>

            {loading && (
                <div className="absolute top-0 left-0 w-full h-full bg-gray-800 bg-opacity-50 flex justify-center items-center z-40">
                    <div className="loader">Loading...</div>
                </div>
            )}
        </div>
    );
}

export default Analysis;
