import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars , faTimes} from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import SideBar from './SideBar'; // Import the SideBar component
import axios from 'axios';
import { Line } from 'react-chartjs-2';

function Analysis() {
    const navigate = useNavigate();
    const [sidebarOpen, setSidebarOpen] = useState(true);
    const [inputtedAudio, setInputtedAudio] = useState(null);
    const [loading, setLoading] = useState(false);
    const [employeeData, setEmployeeData] = useState({ firstName: '' });

    let audioFiles = null;

    let analysisData = [[{'id': 0, 'seek': 0, 'start': 0.0, 'end': 2.0, 'text': " Hello, I'm speaking to Amma.", 'emotion' : 'confused', 'tokens': [50364, 2425, 11, 286, 478, 4124, 281, 2012, 1696, 13, 50464], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}, {'id': 1, 'seek': 0, 'start': 4.5200000000000005, 'end': 8.66, 'text': ' This is Prithvi calling from Boston Institute of Analytics at Madhavalli campus.','emotion': 'happy', 'tokens': [50590, 639, 307, 2114, 355, 4917, 5141, 490, 12333, 9446, 295, 25944, 412, 5326, 71, 706, 336, 72, 4828, 13, 50797], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}], [{'id': 0, 'seek': 0, 'start': 0.0, 'end': 2.0, 'text': " Hello, I'm speaking to Amma.", 'emotion' : 'angry', 'tokens': [50364, 2425, 11, 286, 478, 4124, 281, 2012, 1696, 13, 50464], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}, {'id': 1, 'seek': 0, 'start': 4.5200000000000005, 'end': 8.66, 'text': ' This is Prithvi calling from Boston Institute of Analytics at Madhavalli campus.', 'emotion' : 'neutral', 'tokens': [50590, 639, 307, 2114, 355, 4917, 5141, 490, 12333, 9446, 295, 25944, 412, 5326, 71, 706, 336, 72, 4828, 13, 50797], 'temperature': 0.0, 'avg_logprob': -0.6953301429748535, 'compression_ratio': 1.416243654822335, 'no_speech_prob': 0.29248109459877014, 'speaker': 'SPEAKER 2'}]]

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const handleEmployeeDataChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData({
            ...employeeData,
            [name]: value,
        });
    };

    function processData(data) {
        let speaker1Data = [], speaker2Data = [];

        data.forEach(call => {
            call.forEach(segment => {
                if (segment.speaker === 'SPEAKER 1') {
                    speaker1Data.push({ time: segment.start, emotion: segment.emotion });
                } else if (segment.speaker === 'SPEAKER 2') {
                    speaker2Data.push({ time: segment.start, emotion: segment.emotion });
                }
            });
        });

        return { speaker1Data, speaker2Data };
    }

    function prepareGraphData(speakerData) {
        const labels = speakerData.map(d => `${d.time}`);
        const data = speakerData.map(d => emotionToValue(d.emotion));
        return { labels, data };
    }

    function emotionToValue(emotion) {
        switch (emotion) {
            case 'happy': return 1;
            case 'angry': return 2;
            case 'confused': return 3;
            case 'neutral': return 4;
            default: return 0;
        }
    }

    function renderGraph(speakerGraphData) {
        const chartData = {
            labels: speakerGraphData.labels,
            datasets: [{
                label: 'Emotions Over Time',
                data: speakerGraphData.data,
                // Additional styling options can be added here
            }]
        };

        return <Line data={chartData} />;
    }

    const { speaker1Data, speaker2Data } = processData(analysisData);
    const speaker1GraphData = prepareGraphData(speaker1Data);
    const speaker2GraphData = prepareGraphData(speaker2Data);

    const handleAudioFileChange = (e) => {
        audioFiles = e.target.files;
        setInputtedAudio(audioFiles);
    };

    const sendAudioFilesToBackend = async () => {
        const formData = new FormData();
        for (const file of inputtedAudio) {
            formData.append('audioFiles', file);
        }

        try {
            const response = await axios.post('http://localhost:5000/upload-audio', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            console.log(response);

            if (response.status === 200) {
                console.log('Audio files uploaded successfully');
                return true;
            } else {
                console.error('Failed to upload audio files');
                return false;
            }
        } catch (error) {
            console.error('An error occurred while uploading audio files:', error);
            return false;
        }
    };

    const handleUploadAudio = async () => {
        setLoading(true);
        const success = await sendAudioFilesToBackend();

        if (success) {
            alert('Audio files uploaded and processed');
        } else {
            alert('Failed to upload audio files');
        }

        setLoading(false);
    };

    return (
        <div className="flex">
            <SideBar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />

            {/* Main content area */}
            <div className={`flex-1 p-10 transition-margin duration-300 ease-in-out ${sidebarOpen ? 'ml-64' : 'ml-0'}`}>
            <button onClick={toggleSidebar} className="text-white absolute top-0 right-0 mt-2 mr-2">
                    <FontAwesomeIcon icon={sidebarOpen ? faTimes : faBars} size="lg" />
            </button>
                {/* Analysis Content */}
                <h1 className="text-2xl font-bold mb-4">Analysis Content</h1>
                <div>
                    <h2 className="text-xl mb-3">Audio Upload</h2>
                    <input
                        type="file"
                        name="audioFiles"
                        accept=".wav, .mp3"
                        multiple
                        onChange={handleAudioFileChange}
                        className="mb-3"
                    />
                    <button onClick={handleUploadAudio} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Upload Audio</button>
                </div>
                <div>
                    {/* Other Analysis functionalities */}
                </div>
            </div>

            <div>
                {/* Render Graphs for Each Speaker */}
                {speaker1GraphData && renderGraph(speaker1GraphData)}
                {speaker2GraphData && renderGraph(speaker2GraphData)}
            </div>

            {/* Loading Overlay */}
            {loading && (
                <div className="absolute top-0 left-0 w-full h-full bg-gray-800 bg-opacity-50 flex justify-center items-center z-40">
                    <div className="loader">Loading...</div>
                </div>
            )}

        </div>
    );
}

export default Analysis;
