import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
    const navigate = useNavigate();
    const [sidebarOpen, setSidebarOpen] = useState(false);
    const [activeTab, setActiveTab] = useState('Dashboard');
    const [inputtedAudio, setInputtedAudio] = useState(null);
    const [loading, setLoading] = useState(false);
    const [employeeData, setEmployeeData] = useState({ firstName: '' });

    let audioFiles = null;

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const handleTabChange = (tabName) => {
        setActiveTab(tabName);
    };

    const handleEmployeeDataChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData({
            ...employeeData,
            [name]: value,
        });
    };

    const handleAudioFileChange = (e) => {
        audioFiles = e.target.files;
        setInputtedAudio(audioFiles);
    };

    const sendAudioFilesToBackend = async () => {
      const formData = new FormData();
  
      // Append each audio file to the FormData object
      for (const file of inputtedAudio) {
          formData.append('audioFiles', file);
      }
  
      try {
          const response = await axios.post('http://localhost:5000/upload-audio', formData, {
              headers: {
                  'Content-Type': 'multipart/form-data'
              }
          });
  
          if (response.status === 200) {
              console.log('Audio files uploaded successfully');
              return true;
          } else {
              console.error('Failed to upload audio files');
              return false;
          }
      } catch (error) {
          console.error('An error occurred while uploading audio files:', error);
          return false;
      }
  };
  

  const handleUploadAudio = async () => {
    try {
        setLoading(true);

        const success = await sendAudioFilesToBackend();

        if (success) {
            alert('Audio files uploaded and processed');
            // Additional logic upon successful upload, e.g., navigate to another page or update state
        } else {
            alert('Failed to upload audio files');
        }
    } catch (error) {
        console.error('An error occurred:', error);
        alert('An error occurred during the upload');
    } finally {
        setLoading(false);
    }
};


    useEffect(() => {
        if (loading) {
            const timeoutId = setTimeout(() => {
                setLoading(false);
                clearTimeout(timeoutId);
                navigate('/analysis');
            }, 10000); // 10 seconds
        }
    }, [loading, navigate]);

    return (
        <div className="flex">
            {/* Sidebar */}
            <div className={`fixed z-30 inset-y-0 left-0 w-64 bg-gray-800 text-white transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out`}>
                <div className="flex items-center justify-between p-4 border-b border-gray-600">
                    <span className="text-xl font-semibold">Dashboard</span>
                    <button onClick={toggleSidebar}>
                        <FontAwesomeIcon icon={faTimes} size="lg" />
                    </button>
                </div>
                <nav className="flex flex-col p-4">
                    <Link to="/dashboard" className="py-2 hover:bg-gray-700 rounded">Dashboard</Link>
                    <Link to="/user-management" className="py-2 hover:bg-gray-700 rounded">User Management</Link>
                    <Link to="/call-analysis" className="py-2 hover:bg-gray-700 rounded">Call Analysis</Link>
                    <Link to="/employee-management" className="py-2 hover:bg-gray-700 rounded">Employee Management</Link>
                </nav>
            </div>

            {/* Content Area */}
            <div className={`flex-1 p-10 ml-${sidebarOpen ? '64' : '0'} transition-margin duration-300 ease-in-out`}>
                {activeTab === 'Dashboard' && (
                    <div>
                        <h1 className="text-2xl font-bold mb-4">Dashboard Content</h1>
                        <h2 className="text-xl mb-3">Audio Upload</h2>
                        <div className="upload-container">
                            <input
                                type="file"
                                name="audio"
                                accept=".wav, .mp3"
                                multiple
                                onChange={handleAudioFileChange}
                                className="mb-3"
                            />
                            <button onClick={handleUploadAudio} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Upload Audio</button>
                        </div>
                    </div>
                )}
                {activeTab === 'Add Employee' && (
                    <div>
                        <h2 className="text-xl font-bold mb-4">Add Employee</h2>
                        <div className="input-container">
                            <input
                                type="text"
                                name="firstName"
                                placeholder="First Name"
                                value={employeeData.firstName}
                                onChange={handleEmployeeDataChange}
                                className="mb-3 p-2 border border-gray-300 rounded"
                            />
                            {/* Add more input fields for employee data */}
                        </div>
                        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Submit</button>
                    </div>
                )}
            </div>

            {/* Loading Overlay */}
            {loading && (
                <div className="absolute top-0 left-0 w-full h-full bg-gray-800 bg-opacity-50 flex justify-center items-center z-40">
                    <div className="loader">Loading...</div>
                </div>
            )}

            {/* Toggle Button */}
            <button className={`fixed z-20 inset-y-0 left-0 p-4 focus:outline-none ${sidebarOpen ? 'hidden' : ''}`} onClick={toggleSidebar}>
                <FontAwesomeIcon icon={faBars} size="lg" className="text-white" />
            </button>
        </div>
    );
}

export default Dashboard;
