import React, { useState } from 'react';
import './Dashboard.css';


function Dashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(false); // Initialize the sidebar state

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Toggle the sidebar state
  };

  const [activeTab, setActiveTab] = useState('Dashboard');
  const [employeeData, setEmployeeData] = useState({
    // Initialize the state for employee data fields
    firstName: '',
    // Add more fields here
  });

  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
  };

  const handleEmployeeDataChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({
      ...employeeData,
      [name]: value,
    });
  };

  const handleAddEmployeeSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send employeeData to the server for storing in MongoDB
      const response = await fetch('/api/add-employee', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(employeeData),
      });

      if (response.status === 200) {
        // Employee data added successfully
        alert('Employee added successfully');
      } else {
        alert('Failed to add employee');
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred');
    }
  };

  return (
    <div>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <button className="close-button" onClick={toggleSidebar}>
          Close Sidebar
        </button>
        <button onClick={() => handleTabChange('Dashboard')}>Dashboard</button>
        <button onClick={() => handleTabChange('Add Employee')}>Add Employee</button>
      </div>

      <div className="content">
        {activeTab === 'Dashboard' && <h1>Dashboard Content</h1>}
        {activeTab === 'Add Employee' && (
          <form onSubmit={handleAddEmployeeSubmit}>
            <h1>Add Employee</h1>
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              value={employeeData.firstName}
              onChange={handleEmployeeDataChange}
            />
            {/* Add more input fields for employee data */}
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
