import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import './Dashboard.css';

function Dashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('Dashboard');
  const [employeeData, setEmployeeData] = useState({
    // Initialize the state for employee data fields
    firstName: '',
    // Add more fields here
  });
  const [audioFiles, setAudioFiles] = useState(null);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
  };

  const handleEmployeeDataChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({
      ...employeeData,
      [name]: value,
    });
  };

  const handleAudioFileChange = (e) => {
    setAudioFiles(e.target.files);
  };

  const handleUploadAudio = async () => {
    try {
      // Create a FormData object to send audio files
      const formData = new FormData();
      for (let i = 0; i < audioFiles.length; i++) {
        formData.append('audio', audioFiles[i]);
      }

      // Send audio data to the server for analysis
      const response = await fetch('/api/analyze-audio', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        // Audio files uploaded and processed successfully
        alert('Audio files uploaded and processed');
      } else {
        alert('Failed to upload audio files');
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred');
    }
  };

  return (
    <div>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <button className="toggle-button" onClick={toggleSidebar}>
          <FontAwesomeIcon icon={sidebarOpen ? faTimes : faBars} />
        </button>
        <button onClick={() => handleTabChange('Dashboard')}>Dashboard</button>
        <button onClick={() => handleTabChange('Add Employee')}>Add Employee</button>
        <button onClick={() => handleTabChange('Audio Upload')}>Audio Upload</button>
      </div>

      <div className="content">
        {activeTab === 'Dashboard' && (
          <div>
            <h1>Dashboard Content</h1>
            <h2>Audio Upload</h2>
            <input
              type="file"
              name="audio"
              accept=".wav, .mp3"
              multiple
              onChange={handleAudioFileChange}
            />
            <button onClick={handleUploadAudio}>Upload Audio</button>
          </div>
        )}
        {activeTab === 'Add Employee' && (
          <div>
            <h2>Add Employee</h2>
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              value={employeeData.firstName}
              onChange={handleEmployeeDataChange}
            />
            {/* Add more input fields for employee data */}
            <button type="submit">Submit</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
